import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [user, setUser] = useState(null);
  const [error, setError] = useState(null);
  const [emails, setEmails] = useState([]);

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await fetch('http://localhost:8080/user', { credentials: 'include' });
        if (response.ok) {
          const data = await response.json();
          setUser(data);
        } else {
          setUser(null);
        }
      } catch (err) {
        setError('Could not connect to the backend. Is it running?');
        setUser(null);
      }
    };
    fetchUser();
  }, []);

  const login = () => {
    window.location.href = 'http://localhost:8080/oauth2/authorization/google';
  };

  const logout = async () => {
    await fetch('http://localhost:8080/logout', { method: 'POST', credentials: 'include' });
    setUser(null);
    setEmails([]);
  };

  const getEmails = async () => {
    try {
      const response = await fetch('http://localhost:8080/api/emails', { credentials: 'include' });
      if (response.ok) {
        const data = await response.json();
        setEmails(data);
      } else {
        console.error("Failed to fetch emails");
      }
    } catch (err) {
      console.error("Error fetching emails:", err);
    }
  };

  if (error) {
    return <div className="App"><header className="App-header"><p>{error}</p></header></div>;
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1>Gmail Plugin</h1>
        {user && user.name !== 'Guest' ? (
          <div>
            <p>Welcome, {user.name}!</p>
            <button onClick={logout}>Logout</button>
            <hr />
            <button onClick={getEmails}>Get Recent Emails with Attachments</button>
            <ul>
              {emails.map((subject, index) => (
                <li key={index}>{subject}</li>
              ))}
            </ul>
          </div>
        ) : (
          <button onClick={login}>Login with Google</button>
        )}
      </header>
    </div>
  );
}

export default App;
